select gid,   sta_code,   name,   type,   owner,   st_askml(the_geom) 
from 
	bpasubstations,   
	ST_MakePolygon(ST_GeomFromText(
		'LINESTRING(-121.2544802316268 48.71001768501529  , -121.193045293847 46.9122176971304  , -119.4641097501147 46.83045216322199  , -119.2694243705624 46.91662824456314  , -119.0706703000426 47.06470454782096  , -118.9102411693901 47.24767498366253  , -118.5335385247419 47.25447214875725  , -118.0911583572046 47.23520018090372  , -117.9682221361734 47.40757646002436  , -117.8781265833163 47.88371426657884  , -118.2350243085375 48.43893909685729  , -118.3598654546816 48.71897136590763  , -118.7747155756468 48.9841702049338  , -119.2845564695109 49.03898640048772  , -121.2544802316268 48.71001768501529  )'  ,
		4269
		)) as r1
	
where ST_Contains( r1,   the_geom) 